mapper.select:
select

FROM specialty b
LEFT JOIN  personality_specialty e ON b.specialty_id = e.specialtyt_id
where ${where}
order by  ${orderBy}
limit  ${offset},${countPerPage}

select

FROM specialty b
LEFT JOIN  vocation_specialty e ON b.specialty_id = e.specialty_id
where ${where}
order by  ${orderBy}
limit ${offset},${countPerPage}

SELECT
	DISTINCT(b.specialty_disciplines) specialtyDisciplines
FROM specialty b
LEFT JOIN  personality_specialty e ON b.specialty_id = e.specialtyt_id
where ${where}
order by  ${orderBy}
limit   ${offset},${countPerPage}

SELECT
	DISTINCT(b.specialty_divided) specialtyDivided
FROM specialty b
LEFT JOIN  vocation_specialty e ON b.specialty_id = e.specialty_id
where ${where}
order by  ${orderBy}
limit   ${offset},${countPerPage}
